package com.exercicios03.java;

public class Exercicio06_NumerosUmVinte {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 20; i++) {
			System.out.println(i);
		}
		
		for (int i = 1; i <= 20; i++) {
			if (i == 20) {
				System.out.print(i);	
			} else	System.out.print(i+", ");
		}
	}
}